INSERT INTO `responses` (`id`, `idtheme`, `datepost`, `name`, `theme`, `email`, `message`, `send`, `answer`, `active`, `sort`) VALUES
(31, 15, '2013-11-03', 'Алексей Петрович', 'Гормон роста', 'cos.solovev@inbox.ru', 'Когда я впервые услышал о гормоне роста, я был ужасно толстым, при этом мышц у меня вообще не было. \nСексуальное желание тоже практически отсутствовало. С помощью терапии Новитал ( я пропил 6 месяцев этот препарат )  после курса я стал другим человеком. Если коротко, то теперь я выгляжу и чувствую себя на 20 лет моложе, а девчонки так и вьются вокруг меня. \nБольшое спасибо, вам удалось изменить мою жизнь. Мне 48 лет ', 0, '', 'on', 310),
(32, 15, '2013-11-04', 'Сергей', 'Спасибо', 'simon_alenikov@mail.ru', 'Мне бы хотелось, чтобы существующие сегодня технологии появились бы раньше, и мне не пришлось бы ждать до 57 лет, чтобы снова обрести вторую молодость,  вернуть себе сексуальную активность. \nПри росте 1,85м и весе 75кг я был очень худым и слабым, все время чувствовал усталость, но с помощью Новитал, следуя советам врачей , я набрал 13кг мышечной массы за 6 недель. Для меня – 57-летнего человека – такое преображение просто чудо. \nА ведь это только начало. Я уверен на все сто, что я смогу еще существеннее улучшить мое тело и качество жизни. Удивительно, но жизнь и в самом деле может только начаться, даже если вам уже 57 лет. \nБольшое спасибо всем разработчикам и врачам за то, что подарили мне новую жизнь.', 0, '', 'on', 320),
(26, 1, '2013-08-28', 'Агран', 'Спасибо Вам за настоящий VIG-RX ! ', 'agran2000@mail.ru', 'Я женатый молодой человек, однако благодаря VIGRX я опять себя чую\nмолодоженом. Было время, когда у нас был секс 2 или 3 раза в неделю, теперь нам\nуже не хватает 3 раза в день. \nОна заявляет, что он стал более, а я хорош как никогда. Виг-ЭрИкс изменил мою жизнь и сохранил огонь в нашем браке. Спасибо Вам.', 0, '', 'on', 280),
(29, 9, '2013-10-24', 'Славик ', 'Потрясный препарат!!!!!!!! ', 'sl200sl@bk.ru', 'Потрясный препарат!!!!!!!! \nРаботает великолепно!!!!!! Уверенность в себе просто супер!!!!! Реально чувствуешь себя плейбоем!!!!! \nДля полного отрыва рекомендую свою любимую схему приёма: утром принимаешь сразу 2 капсулы, через 12 часов 1 капсулу и ещё через 12 часов последнюю капсулу - и ты будешь сам себя бояться (шутка). Но помни, что эффект наступает не сразу, а часов через 5-6, поэтому лучше принимать заранее.\n\n', 0, '', 'on', 290),
(30, 24, '2013-10-31', 'Ярослав', 'Такого оргазма я ещё не испытывал ! ', 'fatemixo@mail.ru', 'Добрый день. \nСпешу выразить Вам свою благодарность, за то что Вы помогли мне познакомиться с вашим препаратом Семенакс. Не передать словами какое я получаю наслаждение  во время секса.\nТеперь моя сексуальная жизнь насыщена всеми красками.', 0, '', 'on', 300),
(1, 16, '2013-07-27', 'Виктория', 'Забыла про женские проблемы!', 'vik_somova77@mail.ru', 'Здравствуйте! \nМне 27 лет, муж старше меня на 15 лет. Сначала эта разница ничуть не смущала, но видно пришло то время когда за семью нужно бороться вместе. У мужа начались проблемы в сексе, сбои эрекции. Решали эту проблему разными способами. \nНо после того как муж увидел рекламу вашего препарата Ярсагумба, решил купить его, выход из ситуации был найден. Сейчас у нас в спальне царит полная гармония, все происходит, как положено. Мы с супругом любим, друг друга, чего и вам желаем. Спасибо Вам!!! \nИ так не обычно было обследоваться без очередей, с хорошим оборудованием и с нормальным отношением к себе, что не жалко заплатить деньги. \nВ итоге у меня выявили кисту на правом яичнике, и назначили лечение, теперь мои ежемесячные несносные боли закончились, и хорошо что я все таки пошла сейчас, а не забросила это дело, т.к. это могло повлиять на репродукцию в будущем.\nТеперь раз в пол года хожу к врачу, наше женское здоровье нужно очень беречь.', 0, '', 'on', 10),
(33, 1, '2013-11-07', 'Виктор', 'Пропил курсовую терапию', 'rvyshegradskij@bk.ru', 'Мне, 65 лет, приобрел препарата 6 упаковок. Имел заболевание простатит и ослабление потенции. Пропил курсовую терапию. Результатом остался доволен и порекомендовал своим коллегам 62года и 60 лет. Они принимают как профилактический курс и считают, что вернулись лет на 20 в более молодые годы. \nВосстановление происходило плавно, без побочных явлений.', 0, '', 'on', 330),
(27, 16, '2013-07-29', 'Aнгелина', 'Мужчинам поможет ?!', 'angelindd9@inbox.ru', 'Когда я прочитала из чего состоит Ярса Гумба я просто развеяла все сомнения, оказвается там в составе есть кордицепиковая кислота которая защищает кожу от свободных радикалов, регулирует микроэкологичекий баланс поверхностного слоя кожи, препятствует росту вредных микроорганизмов, снижает отложения меланина и препятствует образованию пигментных пятен, имеет отбеливающий эффект, его часто используют в китайских кремах, использовали в древности женщины для омоложения лица. \nЭкстракт кордицепса содержит много видов растительных стеринов, которые имеют высокую проницаемость, быстро проникают в кожу, сохраняя влагу на ее поверхности, стимулируют обмен веществ и препятствуют старению клеток. Ингибирует воспаления кожи, старение и предотвращает солнечную эритему. Когда мне подруга из Алтая рассказа об этом, я вполне развеяла сомнения. Может и правду, мужчинам поможет?!', 0, '', 'on', 260),
(28, 1, '2013-08-20', 'Владислав', 'Я стал другим мужчиной ! ', 'vlad300000@yandex.ru', 'Подчеркну -  у меня были сомненья сравнительно VIG-RX. До этого я купил упаковку  VIG-RX, на другом сайте , значительно дешевле. И результата никакого не было. Слава Богу ещё не отравился . Это была китайская подделка ((\nНо здесь я решил , уже гарантированно попробовать , тот препарат , который и выпускает производитель .\nПосле 3-х недель приема у меня осталось лишь одно слово - НЕВЕРОЯТНО. \nМой член стал длиннее, толще и более того о чем я когда-либо мечтал или ждал.\nРанее я чувствовал себя слегка неуверенно и стеснялся общества дам, однако теперь с моей\nновоявленной уверенностью, зная что обладаю \"реальным аппаратом\" я могу подойти\nк любой девушке в баре или ночном клубе и буду чувствовать себя полностью\nуверенно. Частота эрекций и выносливость к тому же увеличились. \nЯ вижу себя полностью иным мужчиной!', 0, '', 'on', 270),
(34, 16, '2013-11-08', 'Алексеев', 'Результаты через 3 недели', 'aleks_dim@mail.ru', 'Добрый день , \nНачал принимать,через 3 недели появились уже первые результаты: устойчивая и продолжительная эрекция. \nСмог провести полноценный половой акт. Улучшилось общее самочувствие и настроение.Результаты от Ярсагумбы очень хорошие. Продолжаю восстановительный курс. \nНадеюсь на полное восстановление.', 0, '', 'on', 340),
